			color: colors[parseInt(Math.random() * colors.length)],
			rotation: r(0, 360, true),
			speed: r(options.speedMin * options.scale, options.speedMax * options.scale),
			yVel: 0,
			gravity: options.gravity * options.scale,
			accel: options.accel,
			shrink: 0.875
